def index_city_centre(request):
        image_paths = []
        for corr in city_centre_corr_list:
          image_paths.append(['City Centre/%04d.jpg' % corr[0], 'City Centre/%04d.jpg' % corr[1]])
        latest_question_list = Question.objects.order_by('-pub_date')[:5]
        context = {'latest_question_list': image_paths}
        return render(request, 'polls/index_city_centre.html', context)

def index_new_college(request):
        image_paths = []
        for corr in new_college_corr_list:
          image_paths.append(['New College/%04d.jpg' % corr[0], 'New College/%04d.jpg' % corr[1]])
        latest_question_list = Question.objects.order_by('-pub_date')[:5]
        context = {'latest_question_list': image_paths}
        return render(request, 'polls/index_new_college.html', context)

def detail(request, question_id):
        try:
                question = Question.objects.get(pk=question_id)
        except Question.DoesNotExist:
                raise Http404("Question does not exist")
        return render(request, 'polls/detail.html', {'question': question})

def vote(request, question_id):
        question = get_object_or_404(Question, pk=question_id)
        try:
                selected_choice = question.choice_set.get(pk=request.POST['choice'])
        except:
                return render(request, 'polls/detail.html', {
                        'question': question,
                        'error_message': "You didn't select a choice."
                })
        else:
                selected_choice.votes += 1
                selected_choice.save()
                return redirect('results', question_id=question.id)


import threading
import numpy as np

class ResultHist():
        _counter_lock = threading.Lock()
        _counter_array = np.zeros(178)

        @classmethod
        def increment_counter(cls, idx):
                with cls._counter_lock:
                        cls._counter_array[idx] += 1

        def some_action(self):
                # core code
                self.increment_counter()

def city_centre_results(request):
        import time
        now = time.localtime()
        time_stamp = "%04d-%02d-%02d-%02d-%02d-%02d," % (now.tm_year, now.tm_mon, now.tm_mday, now.tm_hour, now.tm_min, now.tm_sec)

        submitter = request.POST.get('submitter')
        print(submitter)

        response_array = []
        for i in range(len(city_centre_corr_list)):
                ans = request.POST.get('choice%d' % i)
                if ans == 'Yes':
                    response_array.append(1)
                else:
                    response_array.append(0)
                        #ResultHist.increment_counter(i)

        print(response_array)
        with open('statistics/city_centre/%s%s.txt' % (time_stamp, submitter),'wt') as fp:
                fp.write(str(response_array))


        return render(request, 'polls/results.html', {'response_array':response_array, 'submitter':submitter})

